{
  "components": [
    {
      "name": "Bluetooth Module (HC-05)",
      "type": "Wireless Communication",
      "purpose": "Receives lock/unlock commands from a smartphone via Bluetooth",
      "coordinates": {"x": 0, "y": 0},
      "connections": ["Arduino Microcontroller"]
    },
    {
      "name": "Arduino Nano or Uno",
      "type": "Microcontroller",
      "purpose": "Processes Bluetooth commands and controls the servo or electronic lock",
      "coordinates": {"x": 2, "y": 0},
      "connections": ["Bluetooth Module (HC-05)", "Servo Motor", "LED Indicators", "Power Supply"]
    },
    {
      "name": "Servo Motor",
      "type": "Actuator",
      "purpose": "Physically rotates or pushes the door knob mechanism to lock or unlock",
      "coordinates": {"x": 4, "y": 0},
      "connections": ["Arduino Microcontroller", "Door Knob Mechanism"]
    },
    {
      "name": "Door Knob Mechanism",
      "type": "Mechanical Interface",
      "purpose": "Actual locking hardware; is operated by the servo motor",
      "coordinates": {"x": 6, "y": 0},
      "connections": ["Servo Motor"]
    },
    {
      "name": "Power Supply (Battery or USB)",
      "type": "Power Source",
      "purpose": "Provides power to Arduino, Bluetooth module, and servo",
      "coordinates": {"x": 2, "y": 2},
      "connections": ["Arduino Microcontroller"]
    },
    {
      "name": "LED Indicators (Red/Green)",
      "type": "Status Indicator",
      "purpose": "Provides visual feedback for lock (red) and unlock (green) states",
      "coordinates": {"x": 2, "y": -2},
      "connections": ["Arduino Microcontroller"]
    }
  ]
}
