export default () => (
  <board width="100mm" height="80mm" autorouter="sequential-trace">
    {/* Power nets */}
    <net name="V5" />
    <net name="GND" />

    {/* Arduino Nano */}
    <chip
      name="U1"
      footprint="dip16_wide"
      pinLabels={{
        pin1: "D2",
        pin2: "D3",
        pin3: "D4",
        pin4: "D5",
        pin5: "D6",
        pin6: "D7",
        pin7: "GND",
        pin8: "VCC",
        pin9: "D8",
        pin10: "D9",
        pin11: "D10",
        pin12: "D11",
        pin13: "D12",
        pin14: "A0",
        pin15: "A1",
        pin16: "A2"
      }}
      pcbX={0}
      pcbY={0}
      schX={0}
      schY={0}
    />

    {/* PIR Motion Sensor */}
    <chip
      name="U2"
      footprint="pinrow3"
      pinLabels={{
        pin1: "VCC",
        pin2: "OUT",
        pin3: "GND"
      }}
      pcbX={-35}
      pcbY={-20}
      schX={-5}
      schY={-3}
    />

    {/* Buzzer */}
    <chip
      name="BZ1"
      footprint="pinrow2"
      pinLabels={{
        pin1: "POS",
        pin2: "NEG"
      }}
      pcbX={35}
      pcbY={-20}
      schX={5}
      schY={-3}
    />

    {/* Keypad (4x3) */}
    <chip
      name="U3"
      footprint="pinrow8"
      pinLabels={{
        pin1: "ROW1",
        pin2: "ROW2",
        pin3: "ROW3",
        pin4: "ROW4",
        pin5: "COL1",
        pin6: "COL2",
        pin7: "COL3",
        pin8: "NC"
      }}
      pcbX={0}
      pcbY={-35}
      schX={0}
      schY={-5}
    />

    {/* Status LEDs */}
    <led
      name="LED1"
      color="red"
      footprint="0603"
      pcbX={-20}
      pcbY={20}
      schX={-3}
      schY={3}
    />

    <led
      name="LED2"
      color="green"
      footprint="0603"
      pcbX={-15}
      pcbY={20}
      schX={-2}
      schY={3}
    />

    <led
      name="LED3"
      color="yellow"
      footprint="0603"
      pcbX={-10}
      pcbY={20}
      schX={-1}
      schY={3}
    />

    {/* LED Resistors */}
    <resistor
      name="R1"
      resistance="220"
      footprint="0603"
      pcbX={-20}
      pcbY={25}
      schX={-3}
      schY={4}
    />

    <resistor
      name="R2"
      resistance="220"
      footprint="0603"
      pcbX={-15}
      pcbY={25}
      schX={-2}
      schY={4}
    />

    <resistor
      name="R3"
      resistance="220"
      footprint="0603"
      pcbX={-10}
      pcbY={25}
      schX={-1}
      schY={4}
    />

    {/* Transistor for buzzer control */}
    <transistor
      name="Q1"
      type="npn"
      footprint="sot23"
      pcbX={30}
      pcbY={-10}
      schX={4}
      schY={-1}
    />

    {/* Base resistor for transistor */}
    <resistor
      name="R4"
      resistance="1k"
      footprint="0603"
      pcbX={25}
      pcbY={-10}
      schX={3}
      schY={-1}
    />

    {/* Door/Window sensor input */}
    <pinheader
      name="J1"
      pinCount={2}
      footprint="pinrow2"
      gender="male"
      pitch="2.54mm"
      doubleRow={false}
      showSilkscreenPinLabels={true}
      pinLabels={["SENSOR", "GND"]}
      pcbX={20}
      pcbY={20}
      schX={3}
      schY={3}
    />

    {/* Pull-up resistor for door sensor */}
    <resistor
      name="R5"
      resistance="10k"
      footprint="0603"
      pcbX={20}
      pcbY={25}
      schX={3}
      schY={4}
    />

    {/* Power connections */}
    <trace from=".U1 > .VCC" to="net.V5" />
    <trace from=".U1 > .GND" to="net.GND" />

    {/* PIR sensor connections */}
    <trace from=".U2 > .VCC" to="net.V5" />
    <trace from=".U2 > .GND" to="net.GND" />
    <trace from=".U2 > .OUT" to=".U1 > .D2" maxLength="50mm" />

    {/* Keypad connections */}
    <trace from=".U3 > .ROW1" to=".U1 > .D3" maxLength="40mm" />
    <trace from=".U3 > .ROW2" to=".U1 > .D4" maxLength="40mm" />
    <trace from=".U3 > .ROW3" to=".U1 > .D5" maxLength="40mm" />
    <trace from=".U3 > .ROW4" to=".U1 > .D6" maxLength="40mm" />
    <trace from=".U3 > .COL1" to=".U1 > .D8" maxLength="40mm" />
    <trace from=".U3 > .COL2" to=".U1 > .D9" maxLength="40mm" />
    <trace from=".U3 > .COL3" to=".U1 > .D10" maxLength="40mm" />

    {/* Buzzer control circuit */}
    <trace from=".U1 > .D11" to=".R4 > .pin1" maxLength="30mm" />
    <trace from=".R4 > .pin2" to=".Q1 > .base" maxLength="20mm" />
    <trace from=".Q1 > .collector" to=".BZ1 > .NEG" maxLength="20mm" />
    <trace from=".BZ1 > .POS" to="net.V5" />
    <trace from=".Q1 > .emitter" to="net.GND" />

    {/* LED connections */}
    <trace from=".LED1 > .pos" to=".R1 > .pin1" maxLength="20mm" />
    <trace from=".LED2 > .pos" to=".R2 > .pin1" maxLength="20mm" />
    <trace from=".LED3 > .pos" to=".R3 > .pin1" maxLength="20mm" />

    <trace from=".R1 > .pin2" to=".U1 > .D12" maxLength="40mm" />
    <trace from=".R2 > .pin2" to=".U1 > .A0" maxLength="40mm" />
    <trace from=".R3 > .pin2" to=".U1 > .A1" maxLength="40mm" />

    <trace from=".LED1 > .neg" to="net.GND" />
    <trace from=".LED2 > .neg" to="net.GND" />
    <trace from=".LED3 > .neg" to="net.GND" />

    {/* Door sensor connections */}
    <trace from=".J1 > .SENSOR" to=".U1 > .A2" maxLength="40mm" />
    <trace from=".J1 > .SENSOR" to=".R5 > .pin1" maxLength="20mm" />
    <trace from=".R5 > .pin2" to="net.V5" />
    <trace from=".J1 > .GND" to="net.GND" />
  </board>
)
