export default () => (
  <board width="80mm" height="60mm" autorouter="sequential-trace">
    {/* Power nets */}
    <net name="V5" />
    <net name="GND" />
    <net name="V3_3" />

    {/* Microcontroller - Arduino Nano style */}
    <chip
      name="U1"
      footprint="dip16_wide"
      pinLabels={{
        pin1: "D2",
        pin2: "D3",
        pin3: "D4",
        pin4: "D5",
        pin5: "D6",
        pin6: "D7",
        pin7: "GND",
        pin8: "VCC",
        pin9: "A0",
        pin10: "A1",
        pin11: "A2",
        pin12: "A3",
        pin13: "A4",
        pin14: "A5",
        pin15: "AREF",
        pin16: "RST"
      }}
      pcbX={0}
      pcbY={0}
      schX={0}
      schY={0}
    />

    {/* Soil Moisture Sensor */}
    <chip
      name="U2"
      footprint="pinrow3"
      pinLabels={{
        pin1: "VCC",
        pin2: "GND",
        pin3: "AOUT"
      }}
      pcbX={-25}
      pcbY={-20}
      schX={-5}
      schY={-3}
    />

    {/* Temperature/Humidity Sensor (DHT11) */}
    <chip
      name="U3"
      footprint="pinrow4"
      pinLabels={{
        pin1: "VCC",
        pin2: "DATA",
        pin3: "NC",
        pin4: "GND"
      }}
      pcbX={25}
      pcbY={-20}
      schX={5}
      schY={-3}
    />

    {/* Light Sensor (LDR with voltage divider) */}
    <resistor
      name="R1"
      resistance="10k"
      footprint="0603"
      pcbX={-25}
      pcbY={20}
      schX={-5}
      schY={3}
    />

    <resistor
      name="R2"
      resistance="10k"
      footprint="0603"
      pcbX={-25}
      pcbY={25}
      schX={-5}
      schY={4}
    />

    {/* Status LEDs */}
    <led
      name="LED1"
      color="green"
      footprint="0603"
      pcbX={15}
      pcbY={20}
      schX={3}
      schY={3}
    />

    <led
      name="LED2"
      color="yellow"
      footprint="0603"
      pcbX={20}
      pcbY={20}
      schX={4}
      schY={3}
    />

    <led
      name="LED3"
      color="red"
      footprint="0603"
      pcbX={25}
      pcbY={20}
      schX={5}
      schY={3}
    />

    {/* Current limiting resistors for LEDs */}
    <resistor
      name="R3"
      resistance="330"
      footprint="0603"
      pcbX={15}
      pcbY={25}
      schX={3}
      schY={4}
    />

    <resistor
      name="R4"
      resistance="330"
      footprint="0603"
      pcbX={20}
      pcbY={25}
      schX={4}
      schY={4}
    />

    <resistor
      name="R5"
      resistance="330"
      footprint="0603"
      pcbX={25}
      pcbY={25}
      schX={5}
      schY={4}
    />

    {/* Pull-up resistor for DHT11 */}
    <resistor
      name="R6"
      resistance="4.7k"
      footprint="0603"
      pcbX={30}
      pcbY={-15}
      schX={6}
      schY={-2}
    />

    {/* Power connections */}
    <trace from=".U1 > .VCC" to="net.V5" />
    <trace from=".U1 > .GND" to="net.GND" />

    {/* Soil moisture sensor connections */}
    <trace from=".U2 > .VCC" to="net.V5" />
    <trace from=".U2 > .GND" to="net.GND" />
    <trace from=".U2 > .AOUT" to=".U1 > .A0" maxLength="50mm" />

    {/* DHT11 connections */}
    <trace from=".U3 > .VCC" to="net.V5" />
    <trace from=".U3 > .GND" to="net.GND" />
    <trace from=".U3 > .DATA" to=".U1 > .D2" maxLength="50mm" />
    <trace from=".U3 > .DATA" to=".R6 > .pin1" maxLength="30mm" />
    <trace from=".R6 > .pin2" to="net.V5" />

    {/* Light sensor (LDR) connections */}
    <trace from=".R1 > .pin1" to="net.V5" />
    <trace from=".R1 > .pin2" to=".R2 > .pin1" maxLength="20mm" />
    <trace from=".R2 > .pin2" to="net.GND" />
    <trace from=".R1 > .pin2" to=".U1 > .A1" maxLength="50mm" />

    {/* LED connections */}
    <trace from=".LED1 > .pos" to=".R3 > .pin1" maxLength="20mm" />
    <trace from=".LED2 > .pos" to=".R4 > .pin1" maxLength="20mm" />
    <trace from=".LED3 > .pos" to=".R5 > .pin1" maxLength="20mm" />

    <trace from=".R3 > .pin2" to=".U1 > .D3" maxLength="50mm" />
    <trace from=".R4 > .pin2" to=".U1 > .D4" maxLength="50mm" />
    <trace from=".R5 > .pin2" to=".U1 > .D5" maxLength="50mm" />

    <trace from=".LED1 > .neg" to="net.GND" />
    <trace from=".LED2 > .neg" to="net.GND" />
    <trace from=".LED3 > .neg" to="net.GND" />
  </board>
)
